import React from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import { useState, useEffect } from 'react';

const Dictaphone = () => {
  const [response, setResponse] = useState();


  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  const callGPT = async () => {
    const processedInput = userInput.replace(/(in the form of).*?(?=\.|!|\?|$)/gi, '');

    const prompt = '' + processedInput;

    const request = await fetch("https://api.openai.com/v1/chat/completions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer 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"
      },
      body: JSON.stringify({
        "model": "gpt-3.5-turbo",
        "max_tokens": 500,
        "messages": [
          {
            "role": "system",
            "content": prompt
          },
          {
            "role": "user",
            "content": transcript
          }
        ]
      })
    })
    const data = await request.json();
    const responseText = data.choices && data.choices.length > 0 ? data.choices[0].message.content : "No response from God.";
    setResponse(responseText);
  }

  useEffect(() => {
    console.log(response);
  }, [response]);

  return (
    <div>
      <p>Microphone: {listening ? 'on' : 'off'}</p>
      <button onClick={SpeechRecognition.startListening}>Start</button>
      <button onClick={SpeechRecognition.stopListening}>Stop</button>
      <button onClick={resetTranscript}>Reset</button>
      <buttonon onClick={callGPT}></buttonon>
      <input placeholder='Speak'>{transcript}</input>
    </div>
  );
}


export default Dictaphone;
