import React from 'react';
import SpeechRecognition, { useSpeechRecognition } from 'react-speech-recognition';
import { useState, useEffect, useRef } from 'react';
import {Route, Routes} from 'react-router-dom'

const Dictaphone = () => {
  const [response, setResponse] = useState('');
  const [voice, setVoice] = useState('');

  useEffect(() => {
    console.log(voice)
  }, [voice])

  const inputRef = useRef();

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition
  } = useSpeechRecognition();

  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  const callGPT = async () => {

    const prompt = '';

    const request = await fetch("https://api.openai.com/v1/chat/completions", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Authorization": "Bearer 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"
      },
      body: JSON.stringify({
        "model": "gpt-3.5-turbo",
        "max_tokens": 500,
        "messages": [
          {
            "role": "system",
            "content": prompt
          },
          {
            "role": "user",
            "content": voice
          }
        ]
      })
    })
    const data = await request.json();
    const responseText = data.choices && data.choices.length > 0 ? data.choices[0].message.content : "No response from God.";
    setResponse(responseText);
    let audio = new Audio(`http://localhost:3001/speech?text=${responseText}`);
    audio.play();
    inputRef.current.value = '';
    console.log(responseText)
  }



  return (
    <>
    <Routes>
      <div>
      <div>
        <p style={{ color: listening ? 'green' : 'red' }}>Microphone: {listening ? 'on' : 'off'}</p>
      </div>
      <div>
        <textarea style={{ height: '230px', width: '500px' }} placeholder='Type here or click record' onChange={(e) => { setVoice(e.target.value) }} ref={inputRef}></textarea>
      </div>
      <div>
        <button onClick={() => {
          setVoice(null);
          SpeechRecognition.startListening();
        }}>Record</button>
        <button onClick={() => {
          SpeechRecognition.stopListening();
          setVoice(transcript);
        }}>Stop</button>
        <button onClick={() => {
          resetTranscript();
          setResponse(null);
          setVoice(null);
          inputRef.current.value = null;
        }}>Reset</button>
        <button onClick={() => {
          SpeechRecognition.stopListening();
          setVoice(transcript);
          callGPT();
        }}>Ask</button>
      </div>
      <div style={{ backgroundColor: 'rgb(191, 186, 186)' }}>
        <p>{voice ? `User: ${voice}` : 'No request'}</p>
        <p>{response ? `AI: ${response}` : 'No response'}</p>
      </div>
    </div>
    </Routes>
    </>
  );
}


export default Dictaphone;
